import React, {useState, handleChange, handleSubmit} from 'react'

function Login({loginCheck}){
    const[credentials,setCredentials] = useState({user:'', password:''})
    handleChange = (e) => {
        const[variable,value] = [e.target.name,e.target.value]
        const change = {...credentials, variable: value}
        setCredentials(change)
    }
    
    handleSubmit = (e) => {
        e.preventDefault()
        loginCheck(credentials)
    }

    return(
      <form className='dropdown-log' onSubmit={handleSubmit}>
        <input className='menu-item log-item' type="text" name="user" placeholder='usuario' required onChange={handleChange}/>
