import './Style/App.css';
import Header from './Page/Header/Header';
import Home from './Page/Home/Home';
import Footer from './Page/Footer/Footer';
import About from './Page/AboutUs/About';
import Products from './Components/Content/Products';
import Contact from './Page/Contacts/Contact';
import userCredentials from "../../JSON/user-list.json"

import {
  BrowserRouter,
  Routes,
  Route,
  Link,
  Outlet
} from "react-router-dom";

function App() {

/*     L칩gica de Logeo     */
const[credentials,setCredentials] = useState({user:'', password:''})
const[error,setError] = useState("")

// creo listas con todos usuarios y pass:
const usersCredential = userCredentials
const users = Object.keys(usersCredential)
const usersAccounts = []
const usersPass = []
users.map(e =>{
    usersAccounts.push(userCredentials[e].user);
    usersPass.push(userCredentials[e].password);
})

const loginCheck = credentials => {

    console.log(credentials)

    if (usersAccounts.includes(credentials.user)){
        if (usersPass.includes(credentials.password)){
            console.log("Correcto")} else {
                console.log("Contrase침a incorrecta")
            }
    } else {
        console.log("Usuario incorrecto")
    }
}

/*            */
  return (
    <div classname="App">
      <BrowserRouter>
      <Header loginCheck={loginCheck} />
      <Routes>
        <Route path="/" element={<Home/>}/>
        <Route path="/about" element={<About />} />
        <Route path="/products" element={<Products />} />
        <Route path="/contact-us" element={<Contact />} />
        {/*<Route path="/product/:id" element={<SingleProduct />} />*/}
      </Routes>
      <Footer/>
      </BrowserRouter>
    </div>
  );
}

export default App;
