import React, {useState} from 'react'
import {ReactComponent as DropdownMenuIcon} from "../../Icons/filter.svg"
import users from "../../JSON/user-list.json"
import {CSSTransition} from "react-transition-group"

const DropdownMenu = (props) => {
  const[open, setOpen] = useState(false);
  return (
    <li>
    <a href="#" className='' onClick={()=>setOpen(!open)}>
      <DropdownMenuIcon></DropdownMenuIcon>
    </a>
    {open && props.children}
    </li>
  )
}

function DropdownMenuIU() {
  
  const[logged,setLogged] = useState(true)
  const handler = ()=>{
    setLogged(!logged)}

    function DropDownItem(props){
      return(
        <a href={props.link && props.link} className='menu-item' onClick={props.handler}>
          <img className='user-avatar' src={props.profilePic}></img>
          {props.children}
          <span className='icon-right'>{props.iconRight}</span>
        </a>
      )}
  
    function Login(props){
      return(
          <form>
          <input type="text"></input>
          <span className='icon-right'>{props.iconRight}</span>
          </form>
      )}


  return (
    <div className='dropdown'>

      <CSSTransition in={logged===false} unmountOnExit timeout={500} className="menu-primary">
        <div className="menu">
        <DropDownItem profilePic={users[1].img}>Hola</DropDownItem>
        </div>
      </CSSTransition>

      <CSSTransition in={logged===true} unmountOnExit timeout={500} className="menu-logged">
        <div className="menu">
        <DropDownItem profilePic={users[1].img}>{users[1].name}</DropDownItem>
        <DropDownItem  profilePic={users[1].img} handler={handler}>Cerrar Sesi√≥n</DropDownItem>
        </div>
      </CSSTransition>
    </div>
  )
}

export default {DropdownMenu, DropdownMenuIU}
