import React, {useState} from 'react'
import {ReactComponent as DropdownMenuIcon} from "../../Icons/filter.svg"
import users from "../../JSON/user-list.json"
import {CSSTransition} from "react-transition-group"

const DropdownMenu = (props) => {
  const[open, setOpen] = useState(false);
  return (
    <li>
    <a href="#" className='' onClick={()=>setOpen(!open)}>
      <DropdownMenuIcon></DropdownMenuIcon>
    </a>
    {open && props.children}
    </li>
  )
}

function DropdownMenuIU() {
  
  const[logged,setLogged] = useState(true)
  const handler = ()=>{
    setLogged(!logged)}

  const[login,setLogin] = useState(false)
  const handlerLogin = ()=>{
    setLogin(!login)}

    function DropDownItem(props){
      return(
        <a href={props.link && props.link} className='menu-item' onClick={props.handler}>
          <img className='user-avatar' src={props.profilePic}></img>
          {props.children}
          <span className='icon-right'>{props.iconRight}</span>
        </a>
      )}
  
    function Login(props){
      return(
        <form className='dropdown-log'>
          <input className='menu-item log-item' type="text" name="user" placeholder='usuario' />
          <input className='menu-item log-item' type="text" name="password" placeholder='contraseÃ±a' />
          <input className='menu-item submit' type="submit" value="Ingresar" />
        </form>
      )}


  return (
    <div className='dropdown'>

      <CSSTransition in={login===true} unmountOnExit timeout={100} className="menu-logging">
        <div className="menu">
        <Login></Login>
        </div>
      </CSSTransition>
      
      <CSSTransition in={(logged===false)&(login===false)} unmountOnExit timeout={100} className="menu-primary">
        <div className="menu">
          <DropDownItem profilePic={users[1].img} handler={handlerLogin}>Ingresar</DropDownItem> 
          <DropDownItem link="register" profilePic={users[1].img}>Registrarse</DropDownItem>
          <DropDownItem link="faq" profilePic={users[1].img}>Preguntas Frecuentes</DropDownItem>
        </div>
      </CSSTransition>

      <CSSTransition in={logged===true} unmountOnExit timeout={100} className="menu-logged">
        <div className="menu">
        <DropDownItem profilePic={users[1].img}>{users[1].name}</DropDownItem>
        <DropDownItem>Quiero ofrecer mi vivienda</DropDownItem>
        <DropDownItem>FAQs</DropDownItem>
        <DropDownItem  profilePic={users[1].img} handler={handler}>Cerrar SesiÃ³n</DropDownItem>
        </div>
      </CSSTransition>


    </div>
  )
}

export default {DropdownMenu, DropdownMenuIU}
