import React, {useState} from "react";
import { Link } from "react-router-dom";
import Logo from "../../Icons/Logo.png"
import DropdownMenu from "../../Components/Header/DropdownMenu";
import Searcher from "../../Components/Header/Searcher";
import Nav from "../../Components/Header/Nav";
import userCredentials from "../../JSON/user-list.json"


function Header() {

    /*     Lógica de Logeo     */
    const[credentials,setCredentials] = useState({user:'', password:''})
    const[error,setError] = useState("")

    // creo listas con todos usuarios y pass:
    const usersCredential = userCredentials
    const users = Object.keys(usersCredential)
    const usersAccounts = []
    const usersPass = []
    users.map(e =>{
        usersAccounts.push(userCredentials[e].user);
        usersPass.push(userCredentials[e].password);
    })

    const loginCheck = credentials => {
        
        console.log(credentials)
        
        if (usersAccounts.includes(credentials.user)){
            console.log("Correcto")
        }
    }

    

    return (
        <header class="sticky">
            <div className="header-container">
                <div className="header-logo">
                    <Link class="logo" to={{ pathname: "/"}}>
                        <img src={Logo} alt="logo"></img>
                        <p>Los mejores alquileres</p>
                    </Link>
                </div>
                <div>
                    <Searcher></Searcher>
                </div>
                <div className="header-login">
                    <DropdownMenu.DropdownMenu>
                        <DropdownMenu.DropdownMenuIU loginCheck={loginCheck}></DropdownMenu.DropdownMenuIU>
                    </DropdownMenu.DropdownMenu>
                </div>
            </div>
            <Nav/>
        </header>
    );
}
export default Header;
